import React from 'react';
import './Switch.css';

const Switch = ({ isOn, handleToggle, height}) => {
  return (
    <>
    <input
      checked={isOn}
      onChange={handleToggle}
      className="react-switch-checkbox"
      id={`react-switch-new`}
      type="checkbox"
    />
    <label
      style={{ background: isOn && '#06D6A0', height: height, width: 2 * height}}
      title='Админ'
      className="react-switch-label"
      htmlFor={`react-switch-new`}
    >
      <span className={`react-switch-button`}  style={{height: height, width: height}} />
    </label>
  </>
  );
};

export default Switch;
