import React, { useState , useContext, useRef, useEffect}  from "react";
import parse from 'html-react-parser';
import { Button, Card, Form } from "react-bootstrap";
import { useParams } from "react-router-dom";
import {Context} from '../index';
import ToggleButton from 'react-bootstrap/ToggleButton';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';


const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}


const WikiRedact  = () => {
    const {id} = useParams();
    const {text} = useContext(Context);

    const index = text.texts.findIndex(OneText => OneText.id == id);
    let RedactState = text.texts[index];
   
    const [isRedact, setIsRedact] = useState(true)
    let [title, setTitle] = useState(RedactState.state.title)
    let [redactText, setRedactText] = useState(RedactState.state.text)
    
    const textareaRef = useRef(null);

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = 'auto';
      textareaRef.current.style.height = textareaRef.current.scrollHeight;
    }
  }, [textareaRef]);

    return (

        <Card
            style={{borderRadius: 20, margin: '0px 200px 0px 200px', borderWidth: '5px'}}
        >
            <div style={{margin: 20}}>
                <div className="d-flex" style={{marginBottom: 25}}>
                    <div 
                        style={{marginLeft: 25, fontSize: 25, fontWeight: "lighter"}}
                    >
                        {isRedact?
                            <b>{title}: </b>
                        :
                            <Form.Control
                                value={title}
                                onChange={e => setTitle(e.target.value)}
                            >
                            </Form.Control>
                        }   
                    </div>
                    <ToggleButtonGroup  type="radio" name="options" defaultValue={1} style={{marginLeft: "auto"}}>
                        <ToggleButton
                            variant="outline-dark"
                            id="tbg-radio-1"
                            value={1}
                            onClick = {()=> setIsRedact(true)}
                        >
                            Просмотр
                        </ToggleButton>
                        <ToggleButton 
                            variant="outline-dark"
                            id="tbg-radio-2"
                            value={2}
                            onClick = {()=> setIsRedact(false)}
                        >
                            Редактировать
                        </ToggleButton>
                    </ToggleButtonGroup>
                </div>
                {isRedact?
                    <RenderText str={redactText}/>
                    :
                    <div style={{ minHeight: '100vh', display: 'flex', alignItems: 'center' }}>
                    <Form.Control
                                value={redactText}
                                onChange={e => setRedactText(e.target.value)}
                                as="textarea" 
                                style={{ resize: 'none', overflow: 'hidden' }}
                            >
                    </Form.Control>
                    </div>
                }   
            </div>
        </Card>
    );
};

export default WikiRedact;
