import { useContext } from "react";
import ListGroup from 'react-bootstrap/ListGroup';
import {Context} from '../index';
import {observer} from "mobx-react-lite";
import { Button, Card,CardLink,CloseButton,Image } from "react-bootstrap";
import defaut_awatar from "../img/defaut_awatar.jpg"
import star_black from "../img/star_black.png"
import pencil from "../img/pencil.png"
import parse from 'html-react-parser';

const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}


const StateBar = () => {
    const {text} = useContext(Context)
    const {user} = useContext(Context)
    const userName = "Владислав Филатьев"
    const groupName = "Фишинг"
    let countStart = 45
    let clickStar = false

    const click = () => {
        if (clickStar) {
            countStart -=1;
        }else{
            countStart +=1;
        }

    }


    return(
        <ListGroup>
            {text.texts.map(OneText =>
                <Card
                    className="mb-4"
                >
                    <div className="d-flex">
                        <Image 
                            src = {defaut_awatar}
                            height={50}
                            width={50}
                            roundedCircle
                            style={{margin: '10px 10px 5px 15px'}}
                        />
                        <b style={{marginTop: 20}}>{userName}</b>
                        <CloseButton style={{margin: '10px 10px 10px auto'}}></CloseButton>
                    </div>
                    <div 
                        style={{marginLeft: 15, fontSize: 14, color: '#2A5885'}}
                    >
                        {'#' + groupName}
                    </div>
                    <div style={{marginLeft: 19, fontSize: 25, fontWeight: "lighter"}}>{OneText.title} </div>


                    <div style={{margin: 15}}>
                        <RenderText str= {OneText.text}/>
                    </div>



                    <div className="d-flex">
                    <Card 
                        style={{ cursor: 'pointer', borderRadius: 12, margin: '4px 4px 10px 15px', background: '#EBEDF0' } }
                        onClick={click}
                    > 
                        <div  className="d-flex justify-content-center align-items-center" style={{margin: '0px 10px 0px 10px'}}>
                        <Image 
                            src = {star_black}
                            width={22}
                            style={{margin: 3}}
                        ></Image>
                        <div>{countStart}</div>
                        </div>
                    </Card>
                    <Card  
                        className="d-flex justify-content-center align-items-center"
                        style={{ cursor: 'pointer', borderRadius: 12, margin: '4px 4px 10px 0px', background: '#EBEDF0', width: 50 } }
                    > 
                        <Image 
                            src= {pencil}
                            width={22}
                        ></Image>
                    </Card>
                    </div>
                </Card>             
            )}
      </ListGroup>
    );
};

export default StateBar;
