import React, {useContext, useState} from "react";
import { Card, Image, CloseButton, FormLabel } from "react-bootstrap";
import UsersConteiner from "../wiki/UsersConteiner";
import ListGroup from 'react-bootstrap/ListGroup';
import defaut from '../img/defaut_awatar.jpg'
import { Context } from "../index";
import DeletePanel from "../components/modals/DeleteModalsPanel";
import Switch from "../components/Switch";
import {observer} from "mobx-react-lite";
import Form from 'react-bootstrap/Form';


const Users = observer(() => {
    const {users} = useContext(Context)
    const {user} = useContext(Context)
    const [deleteVisible, setDeleteVisible] = useState(false)


    

    return(
        <Card style={{borderRadius: 20, margin: '0px 80px 0px 80px', backgroundColor: "#EDEEF0"}}>
            <ListGroup>
                {users.users.map(OneUser =>
                    <Card style={{margin: 20,  backgroundColor: "#EDEEF0"}}>
                        <div className="d-flex">
                            <Image
                                src = {defaut}
                                width={90}
                                roundedCircle
                                style={{margin: 20}}
                            ></Image>
                            <div className="flex-column">
                                <div style={{marginTop: '20px', fontSize: 30, fontWeight: "bold"}}>{OneUser.user.fn} {OneUser.user.sn}</div>
                                <div style={{marginLeft: '5px'}}>{OneUser.user.email} </div>
                            </div>
                            <div className="fles-column" style={{marginLeft: 10}}>
                                <div>Средняя оценка: {OneUser.user.mark}</div>
                                <div>Количество статей: {OneUser.user.countState}</div>
                            </div>
                            {user.isAdmin?
                            <div style={{margin: '10px 10px 10px auto'}} className="d-flex">
                            <Form >
                                <div className="d-flex">
                                <Form.Label>
                                    Admin
                                </Form.Label>
                                <Form.Check
                                    type="switch"
                                    id= {OneUser.user.id}
                                    checked = {OneUser.user.isAdmin}
                                    onClick={() => OneUser.user.setIsAdmin(!OneUser.user.isAdmin)}
                                    style={{marginLeft: 5}}
                                />
                                </div>
                            </Form>
                                <CloseButton
                                    onClick={()=> setDeleteVisible(true)}
                                    style={{marginLeft: 10}}
                                ></CloseButton>
                            </div>
                            :
                            <b/>
                        }  
                        </div>
                        <DeletePanel show = {deleteVisible} onHide={() => setDeleteVisible(false)} str={"пользователя"}/>
                    </Card>
                )}   
            </ListGroup>
        </Card>
    );
});

export default Users;
