import {makeAutoObservable} from "mobx"
import Mark from "./Mark"
import State from "./State"

export default class TextWiki{
    constructor(){
        this._group = []
        this._text = []
        this._selectedGroup = {};
        this._selectedUser = {};
        makeAutoObservable(this)
    }

    setSelectedGroup(group){
        this._selectedGroup = group
    }

    setSelectedUser(user){
        this._selectedUser = user
    }

    setGroup(group){
        this._group = group
    }

    setText(splitText){
        const text = splitText.map(temp => temp = 
            {id: temp.id, state: new State(temp.id, temp.title, temp.text),
            userId: temp.userId, userName: temp.user.fn + ' ' + temp.user.sn,
            group: {id: temp.groupId, name: temp.group.name}, mark: new Mark(temp.mark,false)}
        )
        this._text = text
    }

    addText(temp, userName, groupName){
        console.log(temp)
        this._text.push(
            {id: temp.id, state: new State(temp.id, temp.title, temp.text),
            userId: temp.userId, userName: userName,
            group: {id: temp.groupId, name: groupName}, mark: new Mark(0,false)}
        )
    }

    delTextId(id){
        this._text = this._text.filter(text => text.id != id)
    }

    get groups(){
        return this._group
    }

    get texts(){
        return this._text
    }

    get selectedGroup(){
        return this._selectedGroup
    }

    get selectedUser(){
        return this._selectedUser
    }
}
