import React from "react";
import { Card, Image } from "react-bootstrap";
import UsersConteiner from "../wiki/UsersConteiner";
import ListGroup from 'react-bootstrap/ListGroup';
import defaut from '../img/defaut_awatar.jpg'

const Users = () => {
    const users = new UsersConteiner();
    
    return(
        <Card style={{borderRadius: 20, margin: '0px 80px 0px 80px', backgroundColor: "#EDEEF0"}}>
            <ListGroup>
                {users.users.map(user =>
                    <Card style={{margin: 20,  backgroundColor: "#EDEEF0"}}>
                        <div className="d-flex">
                            <Image
                                src = {defaut}
                                width={90}
                                roundedCircle
                                style={{margin: 20}}
                            ></Image>
                            <div className="flex-column">
                                <div style={{marginTop: '20px', fontSize: 30, fontWeight: "bold"}}>{user.fn} {user.sn}</div>
                                <div style={{marginLeft: '5px'}}>{user.email} </div>
                            </div>
                            {user.isAdmin?
                            <CloseButton
                                style={{margin: '10px 10px 10px auto'}}
                                onClick={()=> setDeleteVisible(true)}
                            ></CloseButton>
                            :
                            <b/>
                        }  
                        </div>
                    </Card>
                )}   
            </ListGroup>
        </Card>
    );
};

export default Users;
