import { useContext } from "react";
import ListGroup from 'react-bootstrap/ListGroup';
import {Context} from '../index';
import {observer} from "mobx-react-lite";
import { Card,Image } from "react-bootstrap";

const StateBar = () => {
    const {text} = useContext(Context)
    const userName = "Владислав Филатьев"
    const groupName = "Фишинг"
    return(
        <ListGroup>
            {text.texts.map(OneText =>
                <Card
                    className="mb-4"
                >
                    <div>
                        <Image 
                            src = "img/defaut_awatar.jpg"
                            height={50}
                            width={50}
                            roundedCircle
                            style={{margin: '10px 10px 5px 10px'}}
                        />
                        <b>{userName}</b>
                    </div>
                    <div style={{marginLeft: 10, fontSize: 14, color: '#2A5885'}}>{'#' + groupName}</div>
                    <div style={{marginLeft: 14, fontSize: 25, fontWeight: "lighter"}}>{OneText.title} </div>
                    <div style={{margin: 10}}>{OneText.text}</div>
                </Card>             
            )}
      </ListGroup>
    );
};

export default StateBar;
