import { useContext, useState } from 'react';
import { Card, Form, ToggleButton, ToggleButtonGroup  } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Context } from '../../index';

const RedactUser = ({ show, onHide , user}) => {

  
  const [fName, setFName] = useState(user.fn)
  const [sName, setSName] = useState(user.sn)
  const [email, setEmail] = useState(user.email)
  const [text, setText] = useState(user.text)
  const [isRedact, setIsRedact] = useState(true)


  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Изменение профиля
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <div className='d-flex' >
            <Form.Control
              style={{margin: "10px 0px 10px 5px", borderColor: 'black'}}
              onChange={e => setFName(e.target.value)}
              placeholder= {fName}
            />
            <Form.Control
              style={{margin: "10px 0px 10px 5px" , borderColor: 'black'}}
              onChange={e => setSName(e.target.value)}
              placeholder= {sName}
            />
          </div>
          <Form.Control 
            style={{borderColor: 'black'}}
            className='m-1'
            onChange={e => setEmail(e.target.value)}
            placeholder= {email}
          />
          <Button
            variant="outline-dark"
            style={{margin: '10px 0px 0px 3px'}}
          >
            <div>Изменить пароль</div>
          </Button>
          <Form.Control 
            style={{borderColor: 'black', margin: '13px 0px 0px 3px'}}
            type='file' 
          />
          <hr/>
          <div 
            className='d-flex'
                style={{fontWeight: "bold", margin: '0px 10px 0px 30px', fontSize: 30}}
          >
              О себе:
              <ToggleButtonGroup style={{marginLeft: 'auto'}} type="radio" name="options" defaultValue={1}>
                <ToggleButton
                  variant="outline-dark"
                  id="tbg-radio-1"
                  value={1}
                  onClick = {()=> setIsRedact(true)}
                >
                  Просмотр
                </ToggleButton>
                <ToggleButton 
                  variant="outline-dark"
                  id="tbg-radio-2"
                  value={2}
                  onClick = {()=> setIsRedact(false)}
                >
                  Редактировать
                </ToggleButton>
              </ToggleButtonGroup>
          </div>
            <Card className='mt-1'>
              <Form.Control
                value={text}
                onChange={e => setText(e.target.value)}
                as="textarea" 
                rows={30}
              />
            </Card>

        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="outline-danger"
          onClick={onHide}
        >
          Отмена
        </Button>
        <Button
          variant="outline-success"
          onClick={onHide}
        >
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default RedactUser;
