import React, {useContext, useState} from "react";
import { Button, Card,Image } from "react-bootstrap";
import { useNavigate,  useParams } from 'react-router-dom';
import parse from 'html-react-parser';
import defaut from "../img/defaut_awatar.jpg"
import { WIKIS_ROUTER } from "../utils/consts";
import { Context } from "../index";
import DontFuntional from "../components/modals/DontFuntional";
import { fetchUsers } from "../http/usersAPI";

const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}

const UserPage = () => {
    const {users, user} = useContext(Context)
    const {id} = useParams();
    const userProfil = users.users.find(user => user.id == id).user;
    const navigator = useNavigate()
    const [redact,setRedact] = useState(false);
    console.log(user)
    const isMyProfil = user.id == id;

    useEffect(()=>{
        fetchUsers().then(data => users.setUsers(data))
    }, [])

    return (
        <Card
            style={{borderRadius: 20, margin: '0px 80px 0px 80px', borderWidth: '5px'}}
        >
            <div className="d-flex">
                <Image
                    style={{margin: 50}}
                    src = {defaut}
                    width={300}
                    height={300}
                    roundedCircle
                ></Image>
                <div className="d-flex_column" style={{fontWeight: "bold"}}>
                    <div
                        className="d-flex"
                        style={{margin: '150px 10px 0px 0px', fontSize: 30}}
                    >
                        {userProfil.user.fn} {userProfil.user.sn}
                    </div>
                    <div 
                        style={{margin: '0px 10px 0px 5px', fontSize: 15}}
                    >
                        {userProfil.user.email}
                    </div>
                </div>
                <div style={{marginTop: 10, marginLeft: "auto"}}>
                    {isMyProfil?
                        <Button 
                            style={{marginRight: 10}}
                            variant={"outline-dark"}
                            onClick={()=> setRedact(true)}
                        >
                            Редактировать профиль
                        </Button>
                        :
                        <b/>
                    }
                    <Button
                        style={{marginRight: 10}}
                        variant={"outline-dark"}
                        onClick={()=> navigator(WIKIS_ROUTER)}
                    >
                        Статьи
                    </Button>
                </div>
            </div>
            <div 
                style={{fontWeight: "bold", margin: '0px 10px 0px 30px', fontSize: 30}}
            >
                О себе:
            </div>
            <div style={{margin: 15}}>
                <RenderText str= {userProfil.text}/>
            </div>
            <DontFuntional show = {redact} onHide={() => setRedact(false)}/>
        </Card>
    );
};

export default UserPage;
