import {makeAutoObservable} from "mobx"

class Mark{
    constructor(count,isClick){
        this._count = count;
        this._isClick = isClick;
        makeAutoObservable(this)
    }

    setCount(){
        if (this._isClick){
            this._count-=1;
        }else{
            this._count+=1;
        }
        this._isClick = !this._isClick;
    }
    
    setIsClick(isClick){
        this._isClick = isClick
    }

    get count(){
        return this._count
    }

    get isClick(){
        return this._isClick
    }
}
