import {makeAutoObservable} from "mobx"
import { pushMark } from "../http/markApi";

export default class Mark{
    constructor(textId,count,isClick){
        this._textId = textId
        this._count = count;
        this._isClick = isClick;
        makeAutoObservable(this)
    }

    async setCount(userId){
        if (this._isClick){
            this._count-=1;
        }else{
            this._count+=1;
            await pushMark(userId,this._textId);
        }
        this._isClick = !this._isClick;
    }
    
    setIsClick(isClick){
        this._isClick = isClick
    }

    get count(){
        return this._count
    }

    get isClick(){
        return this._isClick
    }
}
