import React, { useState, useEffect , useContext}  from "react";
import parse from 'html-react-parser';
import { Card,Image } from "react-bootstrap";
import { useParams } from "react-router-dom";
import {Context} from '../index';


const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}


const WikiRedact  = () => {
    const {id} = useParams();
    const {text} = useContext(Context);

    const index = text.texts.findIndex(OneText => OneText.id == id);
    let RedactState = text.texts[index];
   

    return (
        <Card
            style={{borderRadius: 20, margin: '0px 200px 0px 200px', borderWidth: '5px'}}
        >
            <div style={{margin: 20}}>
                    <div 
                        style={{marginLeft: 19, fontSize: 25, fontWeight: "border"}}
                    >
                        {RedactState.state.title} 
                    </div>
                <RenderText str={RedactState.state.text}/>
            </div>
        </Card>
    );
};

export default WikiRedact;
