import {makeAutoObservable} from "mobx"
export default class TextWiki{
    constructor(){
        this._group = [
            {id: 1, name: "Фишинг"},
            {id: 2, name: "FACCT"},
            {id: 3, name: "Бизнес IT" },
            {id: 4, name: "Стажировка"}
        ]
        this._text = [
            {id: 1, title: "Фишинг в мессенжерах",
             text: "dfadf", mark: 5, userId: 1, groupId: 1},
            {id: 2, title: "Анти плогиад",
              text: "ads",
              mark: 2, userId: 2, groupId: 2}
        ]
        this._selectedGroup = {};
        makeAutoObservable(this)
    }

    setSelectedGroup(group){
        this._selectedGroup = group
    }

    setGroup(group){
        this._group = group
    }

    setText(text){
        this._text = text
    }

    get groups(){
        return this._group
    }

    get texts(){
        return this._text
    }

    get selectedGroup(){
        return this._selectedGroup
    }
}
