import { useContext , useState} from "react";
import {Card,CloseButton,Image, ListGroup } from "react-bootstrap";
import { useNavigate } from 'react-router-dom';
import parse from 'html-react-parser';
import {Context} from '../index';
import DeletePanel from "./modals/DeleteModalsPanel";
import { REDACT_ROUTER } from "../utils/consts";
import defaut_awatar from "../img/defaut_awatar.jpg";
import star_black from "../img/star_black.png";
import pencil from "../img/pencil.png";

const RenderText = ({str}) =>{
    return(
        parse(str)
    )
};

const StateBar = () => {
    const {text, user} = useContext(Context)

    const userName = "Владислав Филатьев"
    const groupName = "Фишинг"
    let countStart = 45
    let clickStar = false
    const navigator = useNavigate()

    const click = () => {
        if (clickStar) {
            countStart -=1;
        }else{
            countStart +=1;
        }
    }

    const [deleteVisible, setDeleteVisible] = useState(false)

    return(
        <ListGroup>
            {text.texts.map(OneText =>
                <Card
                    className="mb-4"
                >
                    <div className="d-flex">
                        <Image 
                            src = {defaut_awatar}
                            height={50}
                            width={50}
                            roundedCircle
                            style={{margin: '10px 10px 5px 15px'}}
                        />
                        <b style={{marginTop: 20}}>{OneText.userName}</b>
                        {user.isAdmin?
                            <CloseButton
                                style={{margin: '10px 10px 10px auto'}}
                                onClick={()=> setDeleteVisible(true)}
                            ></CloseButton>
                            :
                            <b/>
                        }  
                    </div>
                    <div 
                        style={{marginLeft: 15, fontSize: 14, color: '#2A5885'}}
                    >
                        {'#' + groupName}
                    </div>
                    <div style={{marginLeft: 19, fontSize: 25, fontWeight: "lighter"}}>{OneText.title} </div>


                    <div style={{margin: 15}}>
                        <RenderText str= {OneText.text}/>
                    </div>



                    <div className="d-flex">
                    <Card 
                        style={{ cursor: 'pointer', borderRadius: 12, margin: '4px 4px 10px 15px', background: '#EBEDF0' } }
                        onClick={click}
                    > 
                        <div  className="d-flex justify-content-center align-items-center" style={{margin: '0px 10px 0px 10px'}}>
                        <Image 
                            src = {star_black}
                            width={22}
                            style={{margin: 3}}
                        ></Image>
                        <div>{countStart}</div>
                        </div>
                    </Card>
                    <Card  
                        className="d-flex justify-content-center align-items-center"
                        style={{ cursor: 'pointer', borderRadius: 12, margin: '4px 4px 10px 0px', background: '#EBEDF0', width: 50 } }
                        onClick={() => navigator(REDACT_ROUTER + "/" + OneText.id)}
                    > 
                        <Image 
                            src= {pencil}
                            width={22}
                        ></Image>
                    </Card>
                    </div>
                    <DeletePanel show = {deleteVisible} onHide={() => setDeleteVisible(false)} str={"статью"}/>
                </Card>             
            )}
      </ListGroup>
    );
};

export default StateBar;
