import { useContext , useState} from "react";
import {Card,CloseButton,Container,Image, ListGroup } from "react-bootstrap";
import parse from 'html-react-parser';
import { useNavigate } from 'react-router-dom';
import {observer} from "mobx-react-lite";
import {Context} from '../index';
import DeletePanel from "./modals/DeleteModalsState";
import defaut_awatar from "../img/defaut_awatar.jpg";
import StarButton from "./controls/StarButton";
import PencilButton from "./controls/PencilButton";
import { USER_ROUTER } from "../utils/consts";

const RenderText = ({str}) =>{
    return(
        parse(str)
    )
};

const StateBar = observer(() => {
    const {text, user} = useContext(Context)
    const [deleteVisible, setDeleteVisible] = useState(false)
    const navigator = useNavigate()
    
    return(
        <Container>
            <Card
                style={{marginBottom: 20}}
            >
                 <Image 
                                    src = {defaut_awatar}
                                    height={30}
                                    width={30}
                                    roundedCircle
                                    style={{margin: '10px 10px 5px 15px'}}
                                />
                Добавить статью
            </Card>
            <ListGroup>
                {text.texts.map(OneText =>
                    <Card
                        className="mb-4"
                    >
                        <div className="d-flex">
                            <div
                                style={{cursor: 'pointer'}}
                                onClick={() => navigator(USER_ROUTER + '/' + OneText.userId)}
                            >
                                <Image 
                                    src = {defaut_awatar}
                                    height={50}
                                    width={50}
                                    roundedCircle
                                    style={{margin: '10px 10px 5px 15px'}}
                                />
                                <b style={{marginTop: 20}}>
                                    {OneText.userName}
                                </b>
                            </div>
                            {user.isAdmin?
                                <CloseButton
                                    style={{margin: '10px 10px 10px auto'}}
                                    onClick={()=> setDeleteVisible(true)}
                                ></CloseButton>
                                :
                                <b/>
                            }  
                        </div>
            
                        <div 
                            style={{marginLeft: 15, fontSize: 14, color: '#2A5885', cursor: 'pointer'}}
                            onClick = {()=> text.setSelectedGroup(OneText.group)}
                        >
                            {'#' + OneText.group.name}
                        </div>

                        <div 
                            style={{marginLeft: 19, fontSize: 25, fontWeight: "lighter"}}
                        >
                            {OneText.state.title} 
                        </div>

                        <div style={{margin: 15}}>
                            <RenderText str= {OneText.state.text}/>
                        </div>

                        <div className="d-flex">
                            <StarButton 
                                mark={OneText.mark}
                            />
                            <PencilButton idState={OneText.state.id}/>
                        </div>

                        <DeletePanel 
                            id={OneText.state.id}
                            show = {deleteVisible} 
                            onHide={() => setDeleteVisible(false)} 
                        />
                
                    </Card>             
                )}
            </ListGroup>
      </Container>
    );
});

export default StateBar;
