import React from "react";
import { Button, Card,Image } from "react-bootstrap";
import UserProfil from "../wiki/UserProfil";
import defaut from "../img/defaut_awatar.jpg"
import parse from 'html-react-parser';

const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}

const UserPage = () => {
    const userProfil = new UserProfil();
    return (
        <Card
            style={{borderRadius: 20, margin: '0px 80px 0px 80px', borderWidth: '5px'}}
        >
            <div className="d-flex">
                <Image
                    style={{margin: 50}}
                    src = {defaut}
                    width={300}
                    height={300}
                    roundedCircle
                ></Image>
                <div className="d-flex_column">
                <div className="d-flex">
                    <div style={{fontWeight: "bold", margin: '280px 10px 0px 0px', fontSize: 30}}>{userProfil.user.fn}</div>
                    <div style={{fontWeight: "bold", margin: '280px 10px 0px 10px', fontSize: 30}}>{userProfil.user.sn}</div>
                </div>
                <div style={{fontWeight: "bold", margin: '0px 10px 0px 5px', fontSize: 15}}>{userProfil.user.email}</div>
                </div>
                <div style={{marginTop: 10, marginLeft: "auto"}}>
                    <Button style={{marginRight: 10}} variant={"outline-dark"}>Редактировать профиль</Button>
                    <Button style={{marginRight: 10}} variant={"outline-dark"}>Статьи</Button>
                </div>
            </div>
            <div style={{fontWeight: "bold", margin: '0px 10px 0px 30px', fontSize: 30}}>О себе:</div>
            <div style={{margin: 15}}>
                        <RenderText str= {userProfil.text}/>
            </div>

        </Card>
    );
};

export default UserPage;
