import { useContext, useState } from 'react';
import { Form } from 'react-bootstrap';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { Context } from '../../index';

const RedactUser = ({ show, onHide , user}) => {

  
  const [fName, setFName] = useState(user.fn)
  const [sName, setSName] = useState(user.sn)



  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Изменение профиля
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <div className='d-flex' >
            <Form.Control
              style={{margin: "10px 0px 10px 5px", borderColor: 'black'}}
              onChange={e => setFName(e.target.value)}
              placeholder= {fName}
            />
            <Form.Control
              style={{margin: "10px 0px 10px 5px" , borderColor: 'black'}}
              onChange={e => setSName(e.target.value)}
              placeholder= {sName}
            />
          </div>
          <Form.Control 
            style={{borderColor: 'black'}}
            className='m-1'
            placeholder= 'email'
          />
          <Button
            variant="outline-dark"
            style={{margin: '10px 0px 0px 3px'}}
          >
            <div>Изменить пароль</div>
          </Button>
          
          <Form.Control 
            className='mt-3'
            type='file' 
          />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button
          variant="outline-danger"
          onClick={onHide}
        >
          Отмена
        </Button>
        <Button
          variant="outline-success"
          onClick={onHide}
        >
          Сохранить
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export default RedactUser;
