import React, { useState , useContext}  from "react";
import parse from 'html-react-parser';
import { Button, Card,Image } from "react-bootstrap";
import { useParams } from "react-router-dom";
import {Context} from '../index';


const RenderText = ({str}) =>{
    return(
        parse(str)
    )
}


const WikiRedact  = () => {
    const {id} = useParams();
    const {text} = useContext(Context);

    const index = text.texts.findIndex(OneText => OneText.id == id);
    let RedactState = text.texts[index];
   
    const [isRedact, setIdRedact] = useState(false)
    

    return (

        <Card
            style={{borderRadius: 20, margin: '0px 200px 0px 200px', borderWidth: '5px'}}
        >
            <div style={{margin: 20}}>
                <div className="d-flex">
                    <div 
                        style={{marginLeft: 25, fontSize: 25, fontWeight: "lighter"}}
                    >
                        <b>{RedactState.state.title}: </b>
                    </div>
                    <Button>
                        Редактировать
                    </Button>
                </div>
                {!isRedact?
                    <RenderText str={RedactState.state.text}/>
                    :
                    RedactState.state.text
                }   
            </div>
        </Card>
    );
};

export default WikiRedact;
