import {makeAutoObservable} from "mobx"
export default class UserProfil{
    constructor(id,fn,sn,img,email,mark = 0,countState = 0, isAdmin = false, text = ""){
        //this._user.id =  id;
        this._user.fn = fn;
        this._user.sn = sn;
        this._user.img = img;
        this._user.email = email;
        this._mark = mark;
        this._countState = countState;
        this._isAdmin = isAdmin;
        this._text = text;
        makeAutoObservable(this)
    }

    setUser(user){
        this._user = user
    }

    setIsAdmin(admin){
        this._isAdmin = admin
    }
    
    setCountState(state){
        this._countState = state
    }

    setMark(mark){
        this._mark = mark
    }
    
    setText(text){
        this._text = text
    }
    
    get user(){
        return this._user
    }
    
    get mark(){
        return this._mark
    }
    
    get IsAdmin(){
        return this._isAdmin
    }
    
    get text(){
        return this._text
    }

    get countState(){
        return this._countState
    }
   
}
