import React, { useContext } from "react";
import { Context } from "../index";
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import Image from 'react-bootstrap/Image';
import {observer} from "mobx-react-lite";
import I from "../img/I.png"
import { useNavigate } from 'react-router-dom';
import { ADMIN_ROUTE, LOGIN_ROUTE, USERS_ROUTER, WIKIS_ROUTER } from "../utils/consts";

const SetNav = observer(() => {
    const {user} = useContext(Context)
    const navigator = useNavigate()
    if (user.isAuth){
        //if (user.isAdmin){
            return(
                <Nav 
                    className="ml-auto" 
                    style={{fontWeight:  'bold', fontSize: 20}}
                >
                    (user.isAdmin?){
                    <Nav.Link
                        onClick={()=> navigator(ADMIN_ROUTE)}
                    >
                        Админ панель
                    </Nav.Link>
                    }
                    <Nav.Link
                        onClick={()=> navigator(WIKIS_ROUTER)}
                    >
                        Статьи
                    </Nav.Link>
                    <Nav.Link
                        onClick={()=> navigator(USERS_ROUTER)}
                    >
                        Пользователи
                    </Nav.Link>
                    <Nav.Link
                        onClick={()=> navigator(LOGIN_ROUTE)}
                    >
                        Выход
                    </Nav.Link>
                </Nav>
            )
        // }else{
        //     return(
        //         <Nav 
        //             className="ml-auto" 
        //             style={{fontWeight:  'bold', fontSize: 20}}
        //         >
        //             <Nav.Link
        //                  onClick={()=> navigator(WIKIS_ROUTER)}
        //             >
        //                 Статьи
        //             </Nav.Link>
        //             <Nav.Link
        //                 onClick={()=> navigator(USERS_ROUTER)}
        //             >
        //                 Пользователи
        //             </Nav.Link>
        //             <Nav.Link
        //                 onClick={()=> navigator(LOGIN_ROUTE)}
        //             >
        //                 Выход
        //             </Nav.Link>
        //         </Nav>
        //     )
        // }
    }else{
        return (
            <Nav 
                className="ml-auto"
                style={{fontWeight:  'bold', fontSize: 20}}
            >
                <Nav.Link 
                    onClick={() => navigator(LOGIN_ROUTE)}
                >
                    Авторизация
                </Nav.Link>
            </Nav>
        )
    }
})

const NavBar = () => {
    const navigator = useNavigate()
    return (
        <Navbar 
            color="white"
            style={{boxShadow:"1px 1px 7px black", marginBottom: "40px"}}
        >
            <Container>
                <Navbar.Brand>
                    <Image
                        src= {I}
                        alt="Business IT"
                        height= "70"
                        rounded 
                        onClick={()=> navigator(WIKIS_ROUTER)}
                    />
                </Navbar.Brand>
                <SetNav />
            </Container>
        </Navbar>
    )
}


export default NavBar;
