import React, { useState, useEffect }  from "react";
import ReactMarkdown from 'react-markdown';



const WikiRedact  = () => {
    
    const renderer = {
        //This custom renderer changes how images are rendered
        //we use it to constrain the max width of an image to its container
        image: ({alt,src,title}) => (
            <img 
                alt={alt} 
                src={src} 
                title={title} 
                style={{ maxWidth: 10 }} 
            />
        ),
    };

    const text = '![temp ](https://kalix.club/uploads/posts/2023-03/1677710138_kalix-club-p-zastavka-na-glavnii-ekran-telefona-oboi-52.jpg)'
    return (
        <ReactMarkdown 
            renderer = {renderer}
        children={text}></ ReactMarkdown>
    );
};

export default WikiRedact;
